  html += "</head><body>";
  html += "<div class='update-indicator' id='updateIndicator' data-i18n='updating'>";
  html += htmlEscape(Texts::updating.str());
  html += "</div>";
  html += "<div class='container'>";
  html += "<div class='header'>";
