  html += "</strong></a>";
  html += "<span class='access-sep'>â€¢</span>";
  html += "<a id='ipAddressLink' class='";
  html += ipAccessClass;
  html += "' href='";
  html += ipAccessHref;
  html += "' data-access-host='";
  html += (ipAvailable ? diagnosticData.ipAddress : String(""));
  html += "' data-secure='";
  html += secureScheme;
  html += "' data-legacy='";
  html += legacyScheme;
  html += "' data-access-label='";
  html += (ipAvailable ? diagnosticData.ipAddress : String(""));
  html += "' data-legacy-label='";
